import sys
from all4one import all_for_one
from PyQt5.QtWidgets import QApplication, QPushButton, QVBoxLayout, QWidget,QDialog

class btn_for_one(QWidget):
    def __init__(self):
        super().__init__()
        self.btn_gui()

    def btn_gui(self):
        self.setWindowTitle('Tout en un')

        # Ajouter un bouton pour lancer la classe all_for_one
        self.btn_all41 = QPushButton('Tout en un')
        self.btn_all41.clicked.connect(self.all_for_one)
        self.lay_all41 = QVBoxLayout()
        self.lay_all41.addWidget(self.btn_all41)
        self.setLayout(self.lay_all41)

        self.show()


    def all_for_one(self):
        dialog = all_for_one()
        if dialog.exec() == QDialog.Accepted:
            dialog.GO()

    def closeEvent(self, event):
        dialog = all_for_one()
        dialog.accept()
        event.accept()


if __name__ == "__main__":
    app = QApplication(sys.argv)
    widget = btn_for_one()
    sys.exit(app.exec_())
