import os
import re
import sys
from PyQt5.QtWidgets import QApplication, QFileDialog, QLabel, QLineEdit, QVBoxLayout, QWidget, QMessageBox, QPushButton



#___  ____ _ _ _ ____ ____    ___  _    ____ _  _ ___
#|__] |  | | | | |___ |__/    |__] |    |__| |\ |  |
#|    |__| |_|_| |___ |  \    |    |___ |  | | \|  |
                

#OPENING | https://www.youtube.com/watch?v=_85LaeTCtV8 :3

class Filigrane(QWidget):
    def __init__(self):
        super().__init__()

        #Je prefere lancer l'appli ici pour éviter les erreurs d'importation
        self.filigrane_popup()



#____ _ _    _ ____ ____ ____ _  _ ____ 
#|___ | |    | | __ |__/ |__| |\ | |___ 
#|    | |___ | |__] |  \ |  | | \| |___ 
          

#Fonction pour selectionner le dossier
    def select_folder(self):
        folder = QFileDialog.getExistingDirectory(self, "Sélectionner un dossier")
        return folder

#Fonction pour ajouter le texte choisi
    def filimaking(self, filigrane_text):

        #On accepte les majuscules et les minuscules mais pas les symboles
        if not re.match("^[a-zA-Z ]*$", filigrane_text):
            QMessageBox.information(self, "Information", "Le filigrane doit contenir uniquement des lettres majuscules ou minuscules.")
            return
        #on appel le dossier
        folder = self.select_folder()
        if not folder:
            return
        
        #On parcours l'ensemble des  dossiers présents dans le répertoire selectionné
        for subdir, _, files in os.walk(folder):
            for file in files: #On vérifie si il y a des images que PIL peut lire, qu'importe l'utilisation de majuscules
                if any(file.lower().endswith(ext) for ext in ['.bmp', '.dib', '.dcx', '.gif', '.im', '.jpg', '.jpe', '.jpeg', '.pcd', '.pcx', '.png', '.pbm', '.pgm', '.ppm', '.psd', '.tif', '.tiff', '.xbm', '.xpm', '.pdf']):
                 
                    #Si oui on importe le chemin du fichier   
                    base, ext = os.path.splitext(file)
                    new_name = f"{base},{filigrane_text}{ext}"

                    #Pour éviter les erreurs on vérifie si le nom de fichier existe déjà, sinon on renomme
                    i = 1
                    
                    while os.path.exists(os.path.join(subdir, new_name)):
                        new_name = f"{base}_NOM EN DOUBLE{i}_{filigrane_text}{ext}"
                        i += 1

                    #Finalement on renomme l'image avec le nouveau nom 
                    try:
                        old_path = os.path.join(subdir, file)
                        new_path = os.path.join(subdir, new_name)
                        os.rename(old_path, new_path)

                    except Exception as e:
                        self.ERRORS(f"Erreur lors du renommage de l'image {file}: {e}")
        QMessageBox.information(self, "Information", "J'ai renommé toutes les images dans le dossier sélectionné.")

#Fonction pour afficher le popup de filigrane
    def filigrane_popup(self):
        self.setWindowTitle("Ajouter de mots à la fin du nom de l'image")

        #Nos widgets
        self.lab_fili = QLabel()
        self.lab_fili.setText("Ajouter des mots à la fin des noms des images :")

        self.ent_filli = QLineEdit()
        self.ent_filli.setPlaceholderText("Ecrivez un filigrane (sans symboles)")

        self.fili_button = QPushButton()
        self.fili_button.setText("Ajouter des mots")

        #On connecte le bouton à la fonction pour conserver le texte 
        self.fili_button.clicked.connect(lambda: self.filimaking(self.ent_filli.text()))
        
        #Puis on place tout dans une grille
        lay_fili = QVBoxLayout()
        lay_fili.addSpacing(10)
        lay_fili.addWidget(self.lab_fili)
        lay_fili.addSpacing(2)
        lay_fili.addWidget(self.ent_filli)
        lay_fili.addSpacing(10)
        lay_fili.addWidget(self.fili_button)
        lay_fili.addSpacing

        #Et on place la grille dans la fenetre
        self.setLayout(lay_fili) 

