import os,re,sys
from PyQt5.QtWidgets import QApplication, QHBoxLayout, QPushButton, QFileDialog, QLabel, QLineEdit, QVBoxLayout,QWidget, QMessageBox


#___  ____ _ _ _ ____ ____    ___  _    ____ _  _ ___
#|__] |  | | | | |___ |__/    |__] |    |__| |\ |  |
#|    |__| |_|_| |___ |  \    |    |___ |  | | \|  |
                

#OPENING | https://www.youtube.com/watch?v=_85LaeTCtV8 :3


class ImageRenamer(QWidget):
    def __init__(self):
        super().__init__()

        #Je prefere lancer l'appli ici pour éviter les erreurs d'importation
        self.pop_replace()


#____ ____ ___  _    ____ ____ ____ ____ 
#|__/ |___ |__] |    |__| |    |___ |__/ 
#|  \ |___ |    |___ |  | |___ |___ |  \ 
                                        

    def select_folder(self):
        #Permet de sélectionner le dossier
        folder = QFileDialog.getExistingDirectory(self, "Sélectionner un dossier")
        return folder
    
    def launch_rename(self):
        #Obtenir le dossier à traiter
        folder = self.select_folder()
        if not folder:
            return

        #Obtenir les expressions régulières depuis les entrées dans l'interface visuelle
        regex = self.txt_replace.text()
        replacement = self.txt_add.text()

        #On recherche ici tout les fichiers dans tout les (sous) dossiers du dossier selectionné
        for root, _, files in os.walk(folder):
            #on analyse fichier par fichier dans tout les fichiers
            for file in files:
                #on vérifie si le fichier est une image selon son extension contenue dans une liste que PIL peut traiter, qu'importe l'utilisationn de majuscule
                if any(file.lower().endswith(ext) for ext in ['.bmp', '.dib', '.dcx', '.gif', '.im', '.jpg', '.jpe', '.jpeg', '.pcd', '.pcx', '.png', '.pbm', '.pgm', '.ppm', '.psd', '.tif', '.tiff', '.xbm', '.xpm', '.pdf']):
                    #Maintenant qu'on a filtrer on récup le chemin complet
                    file_path = os.path.join(root, file)

                    #Puis on renomme en remplaçant les expressions régulières
                    new_file_name = re.sub(regex, replacement, file)
                    new_file_path = os.path.join(root, new_file_name)
                    os.rename(file_path, new_file_path)


        QMessageBox.information(self, "Terminé", "Le traitement est terminé !")


    def pop_replace(self):
        self.setWindowTitle("Renommeur d'images")

        #Nos widgets
        self.lab_info = QLabel()
        self.lab_info.setText("Changer les mots des noms d'images :")

        self.lab_replace = QLabel("Remplacer :")

        self.txt_replace = QLineEdit()
        self.txt_replace.setPlaceholderText("Ecrivez le mot à remplacer")

        self.lab_add = QLabel("par :")

        self.txt_add = QLineEdit()
        self.txt_add.setPlaceholderText("Ecrivez un mot à ajouter")

        self.go_replacer = QPushButton("Remplacer les mots")

        #Connecter le signal "clicked" du bouton à la fonction pour renommer
        self.go_replacer.clicked.connect(self.launch_rename)

        #place les widgets dans une grille
        vlay_replace = QVBoxLayout()
        hlay_replace = QHBoxLayout()

        hlay_replace.addWidget(self.lab_replace)
        hlay_replace.addWidget(self.txt_replace)

        hlay_replace2 = QHBoxLayout()
        hlay_replace2.addWidget(self.lab_add)
        hlay_replace2.addWidget(self.txt_add)

        vlay_replace.addWidget(self.lab_info)

        vlay_replace.addLayout(hlay_replace)
        vlay_replace.addLayout(hlay_replace2)
        vlay_replace.addWidget(self.go_replacer)

        #défini la grille principale
        self.setLayout(vlay_replace)



#____ ____ ____ _  _ ____ ___    _    ____ _  _ _  _ ____ _  _
#|__/ |  | |    |_/  |___  |     |    |__| |  | |\ | |    |__|
#|  \ |__| |___ | \_ |___  |     |___ |  | |__| | \| |___ |  |
                
#ENDING | https://www.youtube.com/watch?v=CgZVrvQZB6U&ab_channel=SECRETGUEST :3

if __name__ == "__main__":
    app = QApplication(sys.argv)
    renamer = ImageRenamer()
    renamer.show()
    sys.exit(app.exec_())
